import React, { Component } from "react";
import IconButton from "@material-ui/core/IconButton";
import "./Login.css";
import { withRouter } from "react-router";
var APIcall = require("../../congfiguration/BookStoreCallAPI");

class SignInForm extends Component {
    constructor(props) {
      super(props);
      this.state = {
        Email : "",
        Password : ""
      }
      this.handleChange = this.handleChange.bind(this);

    }
    handleChange = e => {
      e.preventDefault();
      let target = e.target;
      let value = target.type === "checkbox" ? target.checked : target.value;
      const { name } = e.target;
      let formErrors = { ...this.state.formErrors };
      this.setState({ formErrors, [name]: value }, () => console.log(this.state));
    }
    Sign_Up = event => {
        console.log("abc");
        this.props.history.push({
            pathname: "/signup"
          });
    }
    Sign_In = event =>{
      var Details = {
        NAME: this.state.Name,
        EMAIL_ID: this.state.Email,
        PASSWORD: this.state.Password,
        CONTACT: this.state.Contact,
        IMAGEPATH : this.state.IMAGEPATH
      }
      APIcall.Details(Details).then(res => {
        console.log("after craete data", res.data.data);
      });
      this.props.history.push({
        pathname: "/abc"
      });
    }
  render() {
    return (
      <body className = "Sign_In">
        <div className="Login">
          <h2 className="Email_Lable">Email</h2>
          <div className="Email_Input">
            <input
              className="email"
              name="Email"
              type="Email"
              value = {this.state.Email}
              onChange={this.handleChange}
              placeholder="Enter The Valid Email"
            />
          </div>
          <h2 className="Password_Lable">Password</h2>
          <div className="Password_Input">
            <input
              className="password"
              type="password"
              value = {this.state.password}
              onChange={this.handleChange}
              placeholder="Enter The Valid password"
              name="password"
            />
          </div>
          <div className="Sigin_In_Lable">
            <IconButton style={{ color: "Black" }} onClick = {this.Sign_In} >Sign In</IconButton>
          </div>
          <div className="Sigin_up_Lable">
            <IconButton style={{ color: "Black" }}
            onClick = {this.Sign_Up}
            >Sign Up</IconButton>
          </div>
        </div>
      </body>
    );
  }
}
export default withRouter(SignInForm);
