import React, { Component } from "react";
import IconButton from "@material-ui/core/IconButton";
import Snackbar from "@material-ui/core/Snackbar";
import CloseIcon from "@material-ui/icons/Close";
import { withRouter } from "react-router";
import "./SignUp.css";

class SignUpForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
          open:false,
          setOpen: false
        }
    }
    homePage = (event) =>{
        this.props.history.push({
            pathname: "/"
          });
    }
    Sign_up = (event) => {
      this.setState({ setOpen: true })
    }
  render() {
    return(
      <div className = "abc">
      <form>

            <h3>Name</h3>
            <input
              className="text"
              name="name"
              type="text"
              placeholder="Enter The Valid Name"
            />
            <h3>Last Name</h3>
            <input
              className="text"
              name="lastName"
              type="text"
              placeholder="Enter The Valid Last Name"
            />
            <h3>Email Id</h3>
            <input
              className="text"
              name="email"
              type="text"
              placeholder="Enter The Valid Email"
            />
            <h3>Password</h3>
            <input
              className="text"
              name="password"
              type="text"
              placeholder="Enter The Password"
            />
            <h3>Contact</h3>
            <input
              className="text"
              name="Contact"
              type="text"
              placeholder="Enter The Valid Contact"
            />
            </form>
             <div className="Sign_up" onClick =  {this.Sign_up}>
            <IconButton style={{ color: "Black",size : "40%" }} >Sign Up</IconButton>
          </div>
          <button className = "backToLogin"
          onClick = {this.homePage}>Click To Login</button>

          {this.state.setOpen ? (
              <div>
               <Snackbar
                  anchorOrigin={{
                    vertical: "top",
                    horizontal: "center"
                  }}
                  open={this.state.open}
                  autoHideDuration={2000}
                  onClose={this.handleClose}
                  ContentProps={{
                    "aria-describedby": "message-id"
                  }}
                  message={
                    <span id="message-id">
                    Please Enter All The Required Details
                     </span>
                  }
                  action={[
                    <IconButton
                      key="close"
                      aria-label="Close"
                      color="inherit"
                      onClick={this.handleClose}
                    >
                      <CloseIcon />
                    </IconButton>
                  ]}
                />
              </div>
          ):(
            <div>
            <Snackbar
               anchorOrigin={{
                 vertical: "top",
                 horizontal: "center"
               }}
               open={this.state.open}
               autoHideDuration={2000}
               onClose={this.handleClose}
               ContentProps={{
                 "aria-describedby": "message-id"
               }}
               message={
                 <span id="message-id">
                 Pleaslllllllllllllllllllllllllllllllll
                  </span>
               }
               action={[
                 <IconButton
                   key="close"
                   aria-label="Close"
                   color="inherit"
                   onClick={this.handleClose}
                 >
                   <CloseIcon />
                 </IconButton>
               ]}
             />
           </div>

               )}
          </div>


    )
  }
}
export default SignUpForm;
