import React, { Component } from "react";
import IconButton from "@material-ui/core/IconButton";
// import Snackbar from "@material-ui/core/Snackbar";
// import CloseIcon from "@material-ui/icons/Close";
// import { withRouter } from "react-router";
import "./SignUp.css";
var APIcall = require("../../congfiguration/BookStoreCallAPI");


class SignUpForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
          open:false,
          setOpen: true,
          IMAGEPATH : ""
        }
    }
    homePage = (event) =>{
        this.props.history.push({
            pathname: "/"
          });
    }
    Sign_up = (event) => {
      this.setState({ open: true });
      this.setState({ setOpen: false });
    }
    handleClose = (event, reason) => {

      this.setState({ open: false });
    };

    getfile = async event => {
      this.setState({ FILE: event.target.files[0] });
    const formData = new FormData();
    formData.append("filePath", event.target.files[0]);
    APIcall.getImagePath(formData).then(res => {
      this.setState({ IMAGEPATH: res.data.url });
    });
    }
  render() {    
    return(
      <div className = "abc">
      <form>

            <h3>Full Name</h3>
            <input
              className="text"
              name="name"
              type="text"
              value = {this.state.Name}
              placeholder="Enter The Valid Name"
            />
            <h3>Email Id</h3>
            <input
              className="text"
              name="email"
              type="text"
              value = {this.state.Email}
              placeholder="Enter The Valid Email"
            />
            <h3>Contact</h3>
            <input
              className="text"
              name="Contact"
              type="text"
              value = {this.state.Name}
              placeholder="Enter The Valid Contact"
            />
             <h3>Password</h3>
            <input
              className="text"
              name="password"
              type="text"
              placeholder="Enter The Password"
            />
              <h3>From</h3>
            <input
              className="text"
              name="password"
              type="text"
              placeholder="Enter The Password"
            />
            <div className="FormField">
            <h3>Profile Image</h3>
              <input
                accept="image/*"
                type="file"
                name="filePath"
                onChange={this.getfile}
              />
            </div>
            </form>
             <div className="Sign_up" onClick =  {this.Sign_up}>
            <IconButton style={{ color: "Black",size : "40%" }} >Sign Up</IconButton>
          </div>
          <button className = "backToLogin"
          onClick = {this.homePage}>Click To Login</button>

          </div>


    )
  }
}
export default SignUpForm;
