  constructor(props) {
    super(props);
    this.state = {
      UserDetails : [],
      name : props.history.location.state.name,
      email:props.history.location.state.email
    }
  }

  getchat = (value) => {
    this.props.history.push({
      pathname: "/chat"
    });
  }
